import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "../css/navbar.css";
const Navbar = () => {

  useEffect(() => {
    const token=localStorage.getItem("token");
    fetch('http://localhost:5000/api/v1/authen', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer '+token
      },
    }).then(res => res.json())
      .then(data => {
        if (data.status === 200) {
          alert("giriş başarılı");
          window.location.href = "/";
        } else {
          alert("giriş başarısız");
        }
      }).catch(err => {
        console.log(err);
      });
  }, []);




    return (
      <nav className="navbar navbar-expand-lg">
        <div className="container-fluid">

          <Link to="/" className="navbar-brand"> Yeni İmaj </Link>
          <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link to="/cari" className="nav-link active">
                  Cari-Kayıt
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/fatura" className="nav-link active" aria-current="page" href="#">
                  Fatura
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/tahsilat" className="nav-link active" href="#">
                  Tahsilat
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/muhasebe_fis" className="nav-link active">
                  Muhasebe
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/hesap_ekstresi" className="nav-link active">
                  Hesap Ekstresi
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/mizan" className="nav-link active">
                  Mizan
                </Link>
              </li>
              <li className="nav-item">
                <Link to="/stok" className="nav-link disabled">
                  Stok
                </Link>
              </li>

            </ul>

          </div>
        </div>
      </nav>
    );
  };

  export default Navbar;
