import React, { useEffect, useState } from "react";
import axios from "axios";
const Mizan = () => {
  const [mizan, setMizan] = useState([]);
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/mizan`)
      .then((response) => {
        setMizan(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

