import React, { useState, useEffect } from "react";
import "../css/fatura.css";
import axios from "axios";
import { Link } from "react-router-dom";
const FaturaKayıt = () => {
  const [visible, setVisible] = useState(false);

  // veri tabanına fatura kayıt işlemi
  const [tur, setTur] = useState("");
  const [cari_id, setCari_id] = useState("");
  const [miktar, setMiktar] = useState("");
  const [fiyat, setFiyat] = useState("");
  const [tutar, setTutar] = useState("");
  const [tarih, setTarih] = useState("");

  const kayitEkle = (e) => {
    e.preventDefault();
    if (
      tur === "" ||
      cari_id === "" ||
      miktar === "" ||
      fiyat === "" ||
      tutar === "" ||
      tarih === ""
    ) {
      alert("Lütfen tüm alanları doldurunuz.");
    } else {
      axios
        .post("http://localhost:5000/api/v1/fatura", {
          tur,
          cari_id,
          miktar,
          fiyat,
          tutar,
          tarih,
        })
        .then((res) => {
          if (tur === "ALIŞ") {
            axios
              .post("http://localhost:5000/api/v1/mahsup", {
                cari_id,
                borc: 0,
                alacak: tutar,
                tarih,
              })
              .then((res) => {
                console.log(res);
                console.log("Alış Kaydedildi");
              })
              .catch((err) => {
                console.log(err);
              });
          } else if (tur === "SATIŞ") {
            axios
              .post("http://localhost:5000/api/v1/mahsup", {
                cari_id,
                borc: tutar,
                alacak: 0,
                tarih,
              })
              .then((res) => {
                console.log(res);
                console.log("Satış mahsup Kaydedildi");
              })
              .catch((err) => {
                console.log(err);
              });
          }
          console.log(res.data);
          alert("Fatura Kayıt Edildi");
        })
        .catch((err) => {
          console.log(err);
          console.log("Fatura Kayıt Edilemedi");
        });
    }
  };

  // cari listesi

  const [APIData, setAPIData] = useState([]);
  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/cari`)
      .then((response) => {
        setAPIData(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

  // fatura listesi
  const [faturaData, setFaturaData] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/fatura_liste`)
      .then((response) => {
        setFaturaData(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

  const faturaSil = async (id) => {
    if (window.confirm("Fatura Kayıdını Silmek İstediğinizden Eminmisniz ? ")) {
      const response = await axios.delete(
        `http://localhost:5000/api/v1/fatura/${id}`
      );
      if (response.status === 200) {
        alert("silme işlemi başarılı");
        window.location.reload();
      }
    }
  };

  return (
    <div className="container-fluid">
      <div class="col">
        <button
          id="bbtn"
          className="btn btn-outline-info"
          onClick={() => setVisible(!visible)}
        >
          {visible ? "Kapat" : "Fatura-Kayıt"}
        </button>
      </div>

      <div className={visible ? "row align-items-start" : "d-none"}>
        <div class="col">
          <label class="form-label">İşlem Türü</label>
          <select
            onChange={(e) => setTur(e.target.value)}
            class="form-select"
            name="tur"
          >
            <option value="1">işlem</option>
            <option value="ALIŞ">ALIŞ</option>
            <option value="SATIŞ">SATIŞ</option>
          </select>
        </div>
        <div class="col">
          <label class="form-label">Cari</label>
          <select
            onSelect={(e) => setCari_id(e.target.value)}
            class="form-select"
            name="cari_id"
          >
            {APIData.map((cari) => {
              return (
                
                <option key={cari.id}  value={cari.id}>
                  {cari.adi}
                </option>
              );
            })}
          </select>
        </div>
        <div class="col">
          <label class="form-label">Miktar</label>
          <input
            onChange={(e) => setMiktar(e.target.value)}
            type="text"
            class="form-control"
            name="miktar"
            required
          />
        </div>
        <div class="col">
          <label class="form-label">Fiyat</label>
          <input
            onChange={(e) => setFiyat(e.target.value)}
            type="text"
            class="form-control"
            name="fiyat"
          />
        </div>
        <div class="col">
          <label class="form-label">Tutar</label>
          <input
            onChange={(e) => setTutar(e.target.value)}
            type="text"
            class="form-control"
            name="tutar"
          />
        </div>
        <div class="col">
          <label class="form-label">Tarih</label>
          <input
            onChange={(e) => setTarih(e.target.value)}
            type="date"
            class="form-control"
            name="tarih"
          />
        </div>
        <div class="col">
          <label class="form-label"></label>
          <button
            id="bbtn"
            onClick={kayitEkle}
            type="button"
            class="btn btn-outline-success"
          >
            Kaydet
          </button>
        </div>
      </div>

      <hr></hr>
      <br></br>
      <div className="scrollable">
        <table class="table table-dark table-striped">
          <thead>
            <tr>
              <th scope="col">Fatura no</th>
              <th scope="col">Tür</th>
              <th scope="col">Cari</th>
              <th scope="col">Miktar</th>
              <th scope="col">Fiyat</th>
              <th scope="col">Tutar</th>
              <th scope="col">Tarih</th>
              <th scope="col"></th>
            </tr>
          </thead>
          <tbody>
            {faturaData.map((kayit) => {
              return (
                <tr>
                  <td>{kayit.id}</td>
                  <td>{kayit.tur}</td>
                  <td>{kayit.adi}</td>
                  <td>{kayit.miktar}</td>
                  <td>{kayit.fiyat}</td>
                  <td>{kayit.tutar}</td>
                  <td>{kayit.tarih}</td>
                  <td><button className="btn btn-outline-danger"onClick={() => faturaSil(kayit.id)}>sil</button> |  <Link to={`/fatura/${kayit.id}`}><button className="btn btn-outline-info" >düzenle</button></Link>
                 
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default FaturaKayıt;
