import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/tablo.css";
import {useParams} from 'react-router-dom'
const CariKayit = (props) => {

  const [carikayit, setcariKayit] = useState({
    cariAdi: "",
  });
  const [cariList, setcariList] = useState([]);

  const firmaEkle = (e) => {
    e.preventDefault();
    if (carikayit.cariAdi === "") {
      alert("Lütfen Firma Adını Giriniz");
    } else {
      axios
        .post("http://localhost:5000/api/v1/cari", {
          adi: carikayit,
        })
        .then((res) => {
          console.log(res);
          console.log(res.data);
          alert("Kayıt Başarılı");
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/v1/cari")
      .then((res) => {
        console.log(res);
        console.log(res.data);
        setcariList(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);


  const [cariID, setCariID] = useState({
    id: "",
  });

  const cariSil = (e,index) => {
    e.preventDefault();
    setCariID({
      id: e.target.index
    })
  }
  
  console.log(cariID.id);
  
  return (
    <div className="container-fluid">
      <div class="row">
        <div class="col-1">
          <label class="form-label">Cari Kayıt</label>
        </div>
        <div className="col-4">
          <input
            onChange={(e) => setcariKayit(e.target.value)}
            type="text"
            class="form-control"
            name="adi"
          />
        </div>
        <div className="col">
          <button onClick={firmaEkle} type="submit" class="btn btn-primary">
            Kaydet
          </button>
        </div>
      </div>
      <div className="scrollable">
        <div className="row">
          <div className="col">
            <table class="table table-striped">
              <thead>
                <tr>
                  <th scope="col">cari kodu</th>
                  <th scope="col">Adı</th>
                  <th scope="col"></th>
                </tr>
              </thead>
              <tbody>
                {cariList.map((cari) => {
                  return (
                    <tr key={cari.id}>
                      <td value="id">{cari.id}</td>
                      <td>{cari.adi}</td>
                      <td>
                       <button className="btn btn-primary" onClick={cariSil(cari.id)}>sil</button> | <button className="btn btn-primary" >düzenle</button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CariKayit;
