import React, { useState, useEffect } from "react";
import axios from "axios";
const CariKayit = (props) => {
  const [carikayit, setcariKayit] = useState({
    cariAdi: "",
  });
  const [cariList, setcariList] = useState([]);

  const firmaEkle = (e) => {
    e.preventDefault();
    if (carikayit.cariAdi === "") {
      alert("Lütfen Firma Adını Giriniz");
    }else{
    axios
      .post("http://localhost:5000/api/v1/cari", {
        adi: carikayit,
      })
      .then((res) => {
        console.log(res);
        console.log(res.data);
        alert("Kayıt Başarılı");
      })
      .catch((err) => {
        console.log(err);
      });
    }
  };

useEffect(() => {
    axios
      .get("http://localhost:5000/api/v1/cari")
      .then((res) => {
        console.log(res);
        console.log(res.data);
        setcariList(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
    },[])

  return (
    <div className="container">
      <div class="row">
        <div class="col-4">
          <label class="form-label">
            <h2>Cari Kayıt</h2>
          </label>
          <input
            onChange={(e) => setcariKayit(e.target.value)}
            type="text"
            class="form-control"
            name="adi"
          />
        <br></br>
        <button onClick={firmaEkle} type="submit" class="btn btn-primary">
          Kaydet
        </button>
      </div>
      </div>
      <br />
    </div>
  );
};

export default CariKayit;
