import React, { useState, useEffect } from "react";
import axios from "axios";
const Tahsilat = () => {
  const [cariData, setCariData] = useState([]);

 

 
  const [id, setID] = useState({cariId: "",});
  const [tutar,setTutar] = useState("");
  const [tarih, setTarih] = useState("");
  const [fis, setFis] = useState("");

//   const kayıtEt = (e) => {
//     axios
//     .put(`http://localhost:5000/api/v1/tahsilat/cari/${id.cariId}`,{
//       tutar,
//       tarih,
//     })
//     .then((res) => {
//       console.log(res.data);
//       alert("Kayıt Başarılı");
//     }
//     ).catch((err) => {
//       console.log("Kaydetme Hatası");
//     })
//   };

// console.log(tutar  + tarih);


// cari para aldıysa boç verdiyse alacağa kaydet
  const kayıtEt =(e)=>{
    e.preventDefault();
    if(tutar === "" || tarih === ""){
      alert("Lütfen tüm alanları doldurunuz.");
    }else{


    }

  }

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/cari`)
      .then((response) => {
        setCariData(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

  const [cari, setCari] = useState([]);

  

  const getirCari = (e) => {
    e.preventDefault();
    setID({
      cariId: e.target.value,
    });
  };

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/carim/${id.cariId}`)
      .then((response) => {
        setCari(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, [id.cariId]);

  // console.log(id.cariId);
  console.log(fis);
  
  
  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-3">
          <h5>Cari Seçiniz</h5>
          <select onChange={getirCari} name="cariId" className="form-control">
            {cariData.map((cari) => {
              return (
                <option key={cari.id} value={cari.id}>
                  {cari.adi}
                </option>
              );
            })}
          </select>
        </div>
      </div>
      <hr></hr>
      {cari.map((index) => {
        return (
          <div className="row">
            <div className="col">
              <h5>Cari Kodu:</h5>
              <h5>{index.id}</h5>
            </div>
            <div className="col">
              <h5>Adı:</h5>
              <h5>{index.adi}</h5>
            </div>
            <div className="col">
              <h5>Telefon:</h5>
            </div>
            <div className="col">
              <h5>????</h5>
            </div>
          </div>
        );
      })}
      <hr></hr>
      <div className="row">
        <div className="col-4">
          <select class="form-select"
          onChange={(e)=>{setFis(e.target.value)}}
          >
            <option>Fis Türü</option>
            <option selected value="ODEME_YAP">ÖDEME YAP</option>
            <option value="ODEME_AL">ÖDEME AL</option>
            </select>
          </div>
      </div>
     </div>
  
  );
};

export default Tahsilat;

