import React, { useState, useEffect } from "react";
import axios from "axios";
const Tahsilat = () => {
  const [cariData, setCariData] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/cari`)
      .then((response) => {
        setCariData(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

  
  // useEffect(() => {
  //   axios
  //     .get(`http://localhost:5000/api/v1/cari/${id}`)
  //     .then((response) => {
  //       setCari(response.data);
  //     })
  //     .catch((error) => {
  //       console.log("gelmedi");
  //     });
  // }, []);
  
  const [cari, setCari] = useState([]);
  const [id, setID] = useState({
    cariId: "",
  });
  
  const getirCari=(e)=>{
    e.preventDefault();
    setID({
      cariId: e.target.value,
    });
    axios
    .get(`http://localhost:5000/api/v1/cari/${id.cariId}`)
    .then((response) => {
      setCari(response.data);
    })
    .catch((error) => {
      console.log("gelmedi");
    });
  }
  
  console.log(id.cariId);
  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-3">
          <h5>Cari Seçiniz</h5>
          <select
            onChange={getirCari}
            name="cariId"
            className="form-control"
          >
            {cariData.map((cari) => {
              
              return (
                
              <option key={cari.id} value={cari.id}>{cari.adi}</option>
              )
            })}
          </select>
        </div>
      </div>
      <hr></hr>
      {

      cari.map((index) => {
        return (
      <div className="row">
        <div className="col">
          <h5>Cari Kodu:</h5>
          <h5>{index.id}</h5>
        </div>
        <div className="col">
          <h5>Adı:</h5>
          <h5>{index.adi}</h5>
        </div>
        <div className="col">
          <h5>Telefon:</h5>
        </div>
        <div className="col">
          <h5>????</h5>
        </div>
      </div>
         );
        })}
      <hr></hr>
      <div className="row">
        <div className="col-12">
          <h5>Ödeme Ekle</h5>
        </div>
      </div>
      <div className="row">
        <div className="col-2">
          <input className="col-form-label" placeholder="Tutar"></input>
        </div>
        <div className="col-2">
          <input
            data-date-format="YYYY/DD/MM"
            type="date"
            class="form-control"
            name="tarih"
          />
        </div>
        <div className="col-2">
          <button className="btn btn-outline-primary">Ödeme Yap</button>
        </div>
      </div>
    </div>
  );
};

export default Tahsilat;
