import React,{useState,useEffect} from "react";
import axios from "axios";
const CariKayit = (props) => {

    const [carikayit,setcariKayit] = useState({
        cariAdi:"",
    })

    const firmaEkle = (e) => {
      e.preventDefault();
      axios.post("http://localhost:5000/api/v1/cari", {
        cariAdi: carikayit
      })
      .then(res => {
        console.log(res);
        console.log(res.data);
        alert("Kayıt Başarılı");
      })
      .catch(err => {
        console.log(err);
      })
    }

    const [APIData, setAPIData] = useState([]);

    useEffect(() => {
      axios.get("http://localhost:5000/api/cari")
          .then((response) => {
            setAPIData(response.data);
          }).catch((error) => {
            console.log("gelmedi");
          });
  }, [])
  return (
    <div className="container">
      <div class="row g-3 align-items-center mb-3">
        <label class="form-label">
          <h2>Cari & Firma & okul Kayit</h2>
        </label>
        <input  onChange={e => setcariKayit(e.target.value)} type="text" class="form-control" name="cariAdi" />
      </div>
      <div class="row g-3 align-items-center mb-3">
        <button onClick={firmaEkle} type="submit" class="btn btn-primary">
          Kaydet
        </button>
      </div>
      <br/>
    
        {
        APIData.map((cari) => {
          return (
          <h1>{cari.cariAdi}</h1>
          )
        })}
    
    </div>
  );
};

export default CariKayit;
