import React, { useState, useEffect } from "react";
import axios from "axios";

const Muhasebe = () => {
    // CARİ GETİR
    const [cari, setCari] = useState([]);
    const [cari_id, setCariId] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5000/api/cari").then(res => {
            setCari(res.data);
        }
        );
    }, []);
  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col">
            <div>
                <label className="form-label">Cari Seç</label>
                <select
                    className="form-control"
                    onChange={(e) => {
                        setCariId(e.target.value);
                    }
                    }
                >
                    {cari.map((cari) => (
                        <option key={cari.id} value={cari.id}>
                            {cari.cari_adi}
                        </option>
                    ))}
                </select>
            </div>
        </div>
      </div>
      <div className="col">
        <table class="table">
          <thead>
            <tr>
              <th scope="col">Tahsilat no</th>
              <th scope="col">Cari Adı</th>
              <th scope="col">Borç</th>
              <th scope="col">Alacak</th>
              <th scope="col">Tarih</th>
              <th scope="col">Alacak-Bakiye</th>
              <th scope="col">Borç-Bakiye</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              <td>Mark</td>
              <td>Otto</td>
              <td>@mdo</td>
              <td>@mdo</td>
              <td>@mdo</td>
              <td>@mdo</td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Muhasebe;
