import React,{useState,useEffect} from 'react'
import axios from 'axios'
import {Link,useParams} from 'react-router-dom'


const CariDüzenleme = () => {
  const [cariUp,setCariUp] = useState([])
  const [cari,setCari] = useState([])
  const {id} = useParams()

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/cari/${id}`)
      .then((res) => {
        console.log(res)
        setCari(res.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }, [])
        
  return (
    <div >
      <div className='container-fluid' >
        <div className='col-4'>
          <label>Cari Adı {id}</label>
          <input
          onChange={(e) => setCariUp(e.target.value)}
          
          type='text' 
          className='form-control'/>
          
          </div>
          <br></br>
          <div className='col'>
            <button className='btn btn-outline-info'>Güncelle</button>
          </div>
      </div>
    </div>
  )
}

export default CariDüzenleme
