import React, { useState, useEffect } from "react";
import axios from "axios";

const Tahsilat = () => {
  const [tahsilat, setTahsilat] = useState([]);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/tahsilatlarim`)
      .then((response) => {
        setTahsilat(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col">merhaba</div>
        <div className="col">merhaba</div>
        <div className="col">merhaba</div>
        <br></br>
        <hr></hr>
        <br />
        <div className="col">
          <table class="table table-bordered border-primary">
            <thead>
              <tr>
                <th scope="col">cari id</th>
                <th scope="col">Tür</th>
                <th scope="col">Cari</th>
                <th scope="col">fatura no</th>
                <th scope="col">Borc</th>
                <th scope="col">Alacak</th>
                <th scope="col">Bakiye</th>
                <th scope="col">Tarih</th>
                <td>ödeme</td>
              </tr>
            </thead>
            {tahsilat.map((kayit) => {
              return (
                <tbody>
                  <tr value={kayit.id}>
                    <td>{kayit.id}</td>
                    <td>{kayit.tur}</td>
                    <td value={kayit.cari_id} >{kayit.adi}</td>
                    <td>{kayit.fatura_id}</td>
                    <td>{kayit.borc}</td>
                    <td>{kayit.alacak}</td>
                    <td>{kayit.bakiye}</td>
                    <td>{kayit.tarih}</td>
                    <td><button className="btn btn-outline-info">Ödeme yap</button></td>
                  </tr>
                </tbody>
              )
            })}
            
          </table>
        </div>
      </div>
    </div>
  );
};

export default Tahsilat;
