import React,{useState,useEffect} from 'react'
import axios from 'axios'
import {Link,useParams} from 'react-router-dom'

const initialState = {
    adi:"",
}

const CariDüzenleme = () => {
  const [cariUp,setCariUp] = useState([])
  const [cari,setCari] = useState([])

  const[state,setState] = useState(initialState)
  const {adi}=state

  const {id} = useParams();

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/carim/${id}`)
      .then((res) => {
        console.log(res)
        setState({...res.data[0]})
      })
      .catch((err) => {
        console.log(err)
        console.log("gelmedi");
      })
  }, [id])

  
  return (
    <div >
      <div className='container-fluid' >
        <div className='col-4'>
          <label>Cari Adı</label>
          <input
          onChange={(e)=>setState({...state,adi:e.target.value})}
          type='text' 
          name='adi'
          className='form-control'
          value={adi}
          />
     
          </div>
          <br></br>
          <div className='col'>
            <button className='btn btn-outline-info'>Güncelle</button>
          </div>
      </div>
    </div>
  )
}

export default CariDüzenleme
