import React, { useState } from "react";
import "../css/fatura.css";
import axios from "axios";
const FaturaKayıt = () => {
  const [visible, setVisible] = useState(false);

  // veri tabanına fatura kayıt işlemi
  const [tur, setTur] = useState("");
  const [cari_id, setCari_id] = useState("");
  const [miktar, setMiktar] = useState("");
  const [fiyat, setFiyat] = useState("");
  const [tutar, setTutar] = useState("");
  const [tarih, setTarih] = useState("");

  const kayitEkle = (e) => {
    e.preventDefault();
     axios.post("http://localhost:5000/api/v1/fatura", {
        tur,
        cari_id,
        miktar,
        fiyat,
        tutar,
        tarih
      })
      .then((res) => {
        console.log(res.data);
        alert("Kayıt Başarılı");
      })
      .catch((err) => {
        console.log("Kaydetme Hatası");
      });

  };

  return (
    <div className="container-fluid">
      <div class="col">
        <button
          className="btn btn-outline-info"
          onClick={() => setVisible(!visible)}
        >
          {visible ? "Kapat" : "Kayıt-Ekle"}
        </button>
      </div>

      <div className={visible ? "row align-items-start" : "d-none"}>
        <div class="col">
          <label class="form-label">Tür</label>
          <select class="form-select" aria-label="Default select example">
            <option selected>SATIŞ</option>
            <option value="1">ALIŞ</option>
          </select>
        </div>
        <div class="col">
          <label class="form-label">Cari</label>
          <select class="form-select" aria-label="Default select example">
            <option selected>Süphan</option>
            <option value="1">Ferit</option>
          </select>
        </div>
        <div class="col">
          <label class="form-label">Miktar</label>
          <input type="text" class="form-control" />
        </div>
        <div class="col">
          <label class="form-label">Fiyat</label>
          <input type="text" class="form-control" />
        </div>
        <div class="col">
          <label class="form-label">Tutar</label>
          <input type="text" class="form-control" />
        </div>
        <div class="col">
          <label class="form-label">Tarih</label>
          <input type="text" class="form-control" />
        </div>
        <div class="col">
          <label class="form-label"></label>
          <button onClick={kayitEkle}  type="button" class="btn btn-outline-success">
            Kaydet
          </button>
        </div>
      </div>

      <hr></hr>
      <br></br>

      <table class="table">
  <thead>
    <tr>
      <th scope="col">id</th>
      <th scope="col">Tür</th>
      <th scope="col">Cari</th>
      <th scope="col">Miktar</th>
      <th scope="col">Fiyat</th>
      <th scope="col">Tutar</th>
      <th scope="col">Tarih</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <th scope="row">1</th>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
      <td>@mdo</td>
      <td>@mdo</td>
      <td>@mdo</td>
    </tr>
   
    
  </tbody>
</table>


    </div>
  );
};

export default FaturaKayıt;
