import React, { useState, useEffect } from "react";
import axios from "axios";

const Muhasebe = () => {
  // CARİ GETİR
  const [cari, setCari] = useState([]);
  const [cari_id, setCariId] = useState({
    cariId: "",
  });
  const [mahsup, setMahsup] = useState([]);

  const getirCari = (e) => {
    e.preventDefault();
    setCariId({
      cariId: e.target.value,
    });
  };
  const getirMahsup = (e) => {
    e.preventDefault();
    axios
      .get(`http://localhost:5000/api/v1/mahsup/${cari_id.cariId}`)
      .then((response) => {
        setMahsup(response.data);
      })
      .then(() => {
        console.log(mahsup);
        console.log("başarılı");
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  };

  console.log(cari_id.cariId);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/v1/cari`)
      .then((response) => {
        setCari(response.data);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
  }, []);

  console.log(mahsup);
  //   console.log(cari_id.cariId);

 const  TahsilatSil =(id)=>{
   if(window.confirm("Tahsilat Silmek istediğinize eminmisniz")){
    axios
      .delete(`http://localhost:5000/api/v1/mahsup/${id}`)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log("gelmedi");
      });
    }
  }




  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-1">
          <label className="form-label">Cari Seç</label>
          </div>
        <div className="col">
          <div>
            <select className="form-control" onChange={getirCari} name="cariId">
              {cari.map((cari) => {
                return (
                  <option key={cari.id} value={cari.id}>
                    {cari.adi}
                  </option>
                );
              })}
            </select>
          </div>
        </div>
        <div className="col">
          <button
            className="btn btn-primary"
            onClick={getirMahsup}
            type="button"
          >
            getir
          </button>
        </div>
      </div>
      <div className="col">
        <table class="table">
          <thead>
            <tr>
              <th scope="col">Tahsilat no</th>
              <th scope="col">Cari Adı</th>
              <th scope="col">Borç</th>
              <th scope="col">Alacak</th>
              <th scope="col">Tarih</th>
              <th scope="col">Alacak-Bakiye</th>
              <th scope="col">Borç-Bakiye</th>
            </tr>
          </thead>
          <tbody>
            {mahsup.map((mahsup) => {
              return (
                <tr key={mahsup.id}>
                  <th scope="row">{mahsup.id}</th>
                  <td>{mahsup.cariadi}</td>
                  <td>{mahsup.borc}</td>
                  <td>{mahsup.alacak}</td>
                  <td>{mahsup.tarih}</td>
                  <td>{mahsup.borc_bakiye}</td>
                  <td>{mahsup.alacak_bakiye}</td>
                  <td>
                    <button className="btn btnoutline-danger" onClick={()=>TahsilatSil(mahsup.id)}></button>
                  </td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Muhasebe;
